mapper.select:
SELECT id, agent_template_id, name, owner_type, owner_id, is_only_for_internal, state, birthday
FROM agent
WHERE id = #{id}

mapper.update:
UPDATE agent
name = #{name}, owner_type = #{ownerType}, owner_id = #{ownerId}, is_only_for_internal = #{isOnlyForInternal}, state = #{state}, birthday = #{birthday},
WHERE id = #{id}
